import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as THREE from "three";
class App extends Component {
  componentDidMount() { 
    var objectScale = THREE.Vector3(2,2,2);
    var startingPos = THREE.Vector3(0,-2,0);

    var scene = new THREE.Scene();
    var camera = new THREE.PerspectiveCamera( 125, window.innerWidth/window.innerHeight, 0.1, 1000 );
    var renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setSize( window.innerWidth, window.innerHeight );
    // document.body.appendChild( renderer.domElement );
    // use ref as a mount point of the Three.js scene instead of the document.body
    this.mount.appendChild( renderer.domElement );
    var geometry = new THREE.BoxGeometry( 2,2,2);
    var material = new THREE.MeshBasicMaterial( { color: 0xd3d3d3d3, wireframe:true} );
    //var building = new THREE.Mesh( geometry, material );
    //scene.add( building );s
    for(var i = 0; i < 5; i++){
      var mesh = new THREE.Mesh(geometry, material);
      if (i === 0){
        mesh.position.set(0, -2, 0);
        mesh.scale.set(1.25,0.25,1);
        mesh.rotation.y = THREE.MathUtils.degToRad(45);
        scene.add(mesh);
      }
      else{
        mesh.position.set(0,(-2+(2*0.25)*i),0);
        mesh.scale.set(1.25,0.25,1);
        mesh.rotation.y = THREE.MathUtils.degToRad(45);
        scene.add(mesh);
      }
    }


    camera.position.z = 5;
    var animate = function () {
      requestAnimationFrame( animate );
      renderer.render( scene, camera );
    };
    animate(); //animation for rotating object
  }
  render() {
    return (
      <div ref={ref => (this.mount = ref)} />
    )
  }
}
const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
export default App;
